		{
		Select dropdown = new Select(driver.findElement(By.id("country")));
		dropdown.selectByVisibleText(country);
		}
		//Find textbox and populate City 
		if (driver.findElement(By.id("city")).size != 0)
		{
		WebElement cityline = driver.findElement(By.id("city"));
		cityline.clear();
		cityline.sendKeys(city);
		}
