		{
		WebElement mobileline = driver.findElement(By.id("mobile"));
		mobileline.clear();
		mobileline.sendKeys(mobile);
		}
		//Unselect Email and Mobile (SMS) check boxes
		//Select Credit Card Type
		if (driver.findElement(By.id("creditCardType")).size != 0)
		{
		Select dropdown4 = new Select(driver.findElement(By.id("creditCardType")));
		dropdown4.selectByVisibleText(creditCardType);
		}
		//Find textbox and Credit Card 
