		{
		Select dropdown6 = new Select(driver.findElement(By.id("expYear")));
		dropdown6.selectByVisibleText(expYear);
		}
		//Click on Make Reservation
		log.info "Selenium populated all the input data"

		if (driver.findElement(By.id("guestInfoCTA")).size != 0)
		{
		webParameter = "guestInfoCTA";
		WebElement bookreservationbutton = driver.findElement(By.id("guestInfoCTA"));
		bookreservationbutton.click();
		}
		if (driver.findElement(By.xpath("//*[@id='checkOutConfirmationWrapper']/div[2]/strong")).size != 0)
		{
		log.info ("Copying Reservation number")
		WebElement ReservationNumber = driver.findElement(By.xpath("//*[@id='checkOutConfirmationWrapper']/div[2]/strong"));
		ReservationNumber1 = ReservationNumber.getText();
		}
		log.info "Reservation number is $ReservationNumber1"

		testRunner.testCase.setPropertyValue('ReservationNumber', "$ReservationNumber1")

		SimpleDateFormat DtFormat = new SimpleDateFormat("yyyy-MM-dd");
		Date date = new Date();
		formatteddate = DtFormat.format(date);
		//System.out.println(DtFormat.format(date).toString());

		testRunner.testCase.setPropertyValue('ReservationDate', "$formatteddate")
		log.info ("$ReservationNumber1 added to property")

		driver.close();
context.browserDriver = driver

log.info "	Executing Test step <ExportReservationNumberToExcel"
testRunner.runTestStepByName( "ExportReservationNumberToExcel")

