con:configuration.scriptText:
//This groovy script will be used as an assertion since we can not use XPath and XQuery assertions

import groovy.json.JsonSlurper //Import the function
def response = messageExchange.response.responseContent
def slurper = new JsonSlurper()
def json = slurper.parseText response
//Script above get response content as String and pass it to JsonSlurper which parse this string and produce one big HashMap. 

//In Room Extras - Stay Preferences
assert json.groups[0].subGroups[0].key == "arrival_and_departure"
//Notes about possible fee on In Romm Extras
//assert json.groups[0].subGroups[0].resources.notes == "Additional charges may apply for in-room extras. Please contact the hotel for details. \nSome rooms may not accommodate a crib and/or rollaway bed."
assert json.groups[0].subGroups[0].resources.description == "Arrival and Departure"

//Boolean Stay Prefs
assert json.groups[0].subGroups[0].preferences[0].key == "earlycheckin"
assert json.groups[0].subGroups[0].preferences[0].options[0].key == "nopref"
assert json.groups[0].subGroups[0].preferences[0].options[1].key == "early"
assert json.groups[0].subGroups[0].preferences[0].options[2].key == "late"

//Boolean Stay Prefs
//assert json.groups[0].subGroups[0].preferences[1].key == "rollaway"
//assert json.groups[0].subGroups[0].preferences[1].options[0].key == "true"
//assert json.groups[0].subGroups[0].preferences[1].options[1].key == "false"

