//This groovy script will be used as an assertion since we can not use XPath and XQuery assertions


import groovy.json.JsonSlurper //Import the function
def response = messageExchange.response.responseContent
def slurper = new JsonSlurper()
def json = slurper.parseText response
def currentTestCase =context.testCase



def GetSPCStayPrefKey1 =currentTestCase.getPropertyValue("GetSPCStayPrefKey1");
def GetSPCStayPref1numberofsubgroupKeys =currentTestCase.getPropertyValue("GetSPCStayPref1numberofsubgroupKeys");
def GetSPCStayPref1subgroup1Key =currentTestCase.getPropertyValue("GetSPCStayPref1subgroup1Key");
//def GetSPCStayPref1subgroup1KeyValues =currentTestCase.getPropertyValue("GetSPCStayPref1subgroup1KeyValues");
number=Integer.parseInt("${GetSPCStayPref1numberofsubgroupKeys}")
log.info number
if(GetSPCStayPrefKey1 != null){
	if(GetSPCStayPref1numberofsubgroupKeys != null){
		if(GetSPCStayPref1subgroup1Key != null){
			for(i=1;i<=number;i++){
			def str = currentTestCase.getPropertyValue("GetSPCStayPref1subgroup${i}KeyValues");
		  	String[] items = str.split(",");
		  	int itemCount = items.length;

			log.info itemCount
		  	for(j=0;j<itemCount;j++){
		  		if(items[j]!=null){
		  			assert json.groups[0].subGroups[1].key == "${GetSPCStayPrefKey1}"
		  			log.info "json.groups[0].subGroups[1].preferences[0].options[${j}].key == ${items[j]}"
  				}
  			}
			}
		}
	}
}


