def slurper = new JsonSlurper()
def json = slurper.parseText response
//Script above get response content as String and pass it to JsonSlurper which parse this string and produce one big HashMap. 

//In Room Extras - Stay Preferences
assert json.groups[0].subGroups[0].key == "in_room_extras"
//Notes about possible fee on In Romm Extras
assert json.groups[0].subGroups[0].resources.notes == "Additional charges may apply for in-room extras. Please contact the hotel for details. \nSome rooms may not accommodate a crib and/or rollaway bed."
assert json.groups[0].subGroups[0].resources.description == "In-Room Extras"

//Boolean Stay Prefs
assert json.groups[0].subGroups[0].preferences[0].key == "crib"
assert json.groups[0].subGroups[0].preferences[0].options[0].key == "true"
assert json.groups[0].subGroups[0].preferences[0].options[1].key == "false"

//Boolean Stay Prefs
assert json.groups[0].subGroups[0].preferences[1].key == "rollaway"
assert json.groups[0].subGroups[0].preferences[1].options[0].key == "true"
assert json.groups[0].subGroups[0].preferences[1].options[1].key == "false"

