class ExcelReader {
  def readData() {
        def path = "C:\\Users\\bhuvnesh.goyal\\Desktop\\Bowtie 2B\\TestData.xls";
        InputStream inputStream = new FileInputStream(path);
        Workbook workbook = WorkbookFactory.create(inputStream);
        Sheet sheet = workbook.getSheetAt(0);

        Iterator rowIterator = sheet.rowIterator();
        rowIterator.next()
        Row row;
        def rowsData = []
        while(rowIterator.hasNext()) {
             row = rowIterator.next()
             def rowIndex = row.getRowNum()
             def colIndex;
             def rowData = []

             for (Cell cell : row) {
             	cell.setCellType(Cell.CELL_TYPE_STRING);
                 colIndex = cell.getColumnIndex();

                 switch (cell.getCellType()) {

                case Cell.CELL_TYPE_STRING:
                    rowData[colIndex] = cell.getRichStringCellValue().getString();
                    break;
                case Cell.CELL_TYPE_NUMERIC:
                    rowData[colIndex] = cell.getNumericCellValue();
                    break;
                case Cell.CELL_TYPE_BOOLEAN:
                    rowData[colIndex] = cell.getBooleanCellValue();
                    break;
                case Cell.CELL_TYPE_FORMULA:
                    rowData[colIndex] = cell.getCellFormula();
                    break;

                 }
                  //rowData[colIndex] = cell.getRichStringCellValue().getString();
             }                    
             rowsData << rowData
         }
         rowsData
  }
 }

