public updateexcel(String x, String y) throws Exception{
int i = Integer.parseInt(x);
//Read the spreadsheet that needs to be updated FileInputStream fsIP= new FileInputStream(new File("C:\\Users\\bhuvnesh.goyal\\Desktop\\Bowtie 2B\\TestData.xls"));
HSSFWorkbook wb = new HSSFWorkbook(fsIP); //Access the workbook HSSFSheet worksheet = wb.getSheetAt(0); //Access the worksheet, so that we can update / modify it.
Cell cell = null; // declare a Cell object cell = worksheet.getRow(i).createCell(21); cell = worksheet.getRow(i).getCell(21); // Access the second cell in second row to update the value cell.setCellValue(y); // Get current cell value value and overwrite the value
CellStyle cellStyle = wb.createCellStyle(); CreationHelper createHelper = wb.getCreationHelper(); cellStyle.setDataFormat(createHelper.createDataFormat().getFormat("yyyy-mm-dd")); cell = worksheet.getRow(i).createCell(22); cell = worksheet.getRow(i).getCell(22); cell.setCellValue(new Date()); cell.setCellStyle(cellStyle);
fsIP.close(); //Close the InputStream
FileOutputStream output_file =new FileOutputStream(new File("C:\\Users\\bhuvnesh.goyal\\Desktop\\Bowtie 2B\\TestData.xls")); //Open FileOutputStream to write updates
wb.write(output_file); //write changes
output_file.close(); //close the stream } }
